#!/data/data/com.termux/files/usr/bin/bash
echo -n "üîë Enter password: "
read -s pw
echo
if [ "$pw" != "1234" ]; then
    echo "‚ùå Incorrect password!"
    exit 1
fi
decoded=$(echo "" | base64 -d)
decrypted=""
for ((i=0; i<${#decoded}; i++)); do
    c=${decoded:$i:1}
    k=${pw:$((i%${#pw})):1}
    decrypted+=$(printf "%b" "$(printf '\\x%02x' $(( $(printf "%d" "'$c") ^ $(printf "%d" "'$k") )) )")
done

tmpfile="/data/data/com.termux/files/usr/tmp/tmp_run.sh"
mkdir -p "$(dirname "$tmpfile")"
echo "$decrypted" > "$tmpfile"
chmod +x "$tmpfile"
bash "$tmpfile"
rm -f "$tmpfile"
