#!/usr/bin/env python3
import base64
import getpass
import tempfile
import subprocess
import os

encrypted_data = """"""

password = getpass.getpass("üîë Enter password: ").strip()
try:
    decoded = base64.b64decode(encrypted_data).decode()
    decrypted = ''.join(chr(ord(c) ^ ord(password[i % len(password)])) for i, c in enumerate(decoded))
except Exception as e:
    print("‚ùå Decryption failed!")
    exit(1)

# Write to temporary bash file
with tempfile.NamedTemporaryFile(delete=False, suffix=".sh", mode='w') as tmp:
    tmp.write(decrypted)
    tmp_path = tmp.name

os.chmod(tmp_path, 0o755)
subprocess.run(["bash", tmp_path])
os.remove(tmp_path)
